/* ==UserStyle==
@name           Nightshade
@namespace      github.com/Mintybea/Nightshade
@version        1.0.0
@description    Ripped and modded version of Foxtrek_64's Dark ferzu theme
@author         Mintybea
==/UserStyle== */

@-moz-document domain("www.ferzu.com") {

    input[type="submit"], button.link-button1, a.link-button1 {
    border: 1px solid #b78bff!important;
    background-color: #b78bff;
    color: #fff!important; }

    table.message a {
    color: #ffb0f5; }

    .actionitems h3, .followers h3, h3.actionitem {
    font-family: Abel;
    font-size: 10pt;
    color: #b78bff; }
  body {
    color: #fdfbec; /* Body text color */
  }
    .photostream-overquota {
    text-align: center;
    padding: 8px 0 10px;
    background-color: #2d2d2d;
    color: #d8d8d8;
    font-weight: bold;
    margin-top: 8px;
}
   #logindisplay {
       background-color: #6d5d88; }
    .page {
        background-color: #6d5d88; }
    a {
    color: #ffb0f5; /* Orange text*/
      }

    .color-pri {
    color: #b28afe; }

      table.messages a {
    color: #ffb0f5; }
  /* Make comments dark */
  .comment, .comment-add {
    background-color: #222;
  }
    element.style {
    color: #012d7e; }
  div[id="main"] {
    background-color: #333 !important;
  }
  .post-add {
    background-color: #444;
  }

  h1, h2, td.right h2, td.left h2, .editor-label label {
    color: #b78bff;
  }
    .taglabel {
    font-weight: bold;
    color: #b78bff;
}
    .hint {
    color: #ccc;
  }
  .post p.date {
    color: inherit;
  }
    a.link-button1 {
    border: 1px solid #c483bc!important;
    background-color: #c483bc;
    color: #fff!important;
  }
  .post p.date a {
    color: #ffb0f5;
  }
  .post.sticky {
    background: url("https://raw.githubusercontent.com/Foxtrek64/Ferzu-Dark-Stylus/master/sticky-post.png") repeat-x;
  }
  table td.left.navt {
    background: #333;
    background: -moz-linear-gradient(left,#333 64%, #333 64%, #222 100%);
    background: -webkit-gradient(linear,left top,right top,color-stop(64%,#333),color-stop(64%,#333),color-stop(100%,#222));
    background: -webkit-linear-gradient(left,#333 64%,#333 64%,#222 100%);
    background: -o-linear-gradient(left,#333 64%,#333 64%,#222 100%);
    background: -ms-linear-gradient(left,#333 64%,#333 64%,#222 100%);
    background: linear-gradient(to right,#333 64%,#333 64%,#222 100%)
  }
    .quicklists a {
    color: #fff;
  }
    .actionitems a {
    color: #fff;
  }
  .popular-list a {
    color: #ccc;
    background-color: unset;
  }


  table.messages {
    color: #ccc;
  }
  table.messages tr {
    background-color: #333;
  }

  .slider { 
    background-color: #222;
  }

  .jslider .jslider-value {
    background: unset;
  }

  .ui-widget-content {
    background: #333;
    color: #fff;
  }
  td.planbasic {
    background-color: #444;
  }
  .planbasic h3 {
    color: #ccc !important;
  }
  td.planitem {
    background-color: #444;
  }
  .editor-field {
    color: #fff;
  }
  div[id="Buy"] > div:nth-child(5) {
    background-color: unset !important;
  }
  .messages td.msg-photo {
    border-left: 4px solid #444;
  }
  ul.ui-multiselect-checkboxes .ui-state-hover, ul.ui-multiselect-checkboxes .ui-widget-content .ui-state-hover {
    color: #eee;
  }

  span.profile-icon {
    color: #eee;
  }
  .member-thumbnail .miniprofile {
    background-color: #444;
  }
  .member-thumbnail .miniprofile h4 {
    text-shadow: none;
  }
  .member-thumbnail .miniprofile a {
    color: #ed37f0;
  }
  #newsfeed-updates-bar {
    background-color: #424242;
    border: 1px solid #5b4164;
    border-top-color: rgb(192, 1, 255);
    border-right-color: rgb(192, 1, 255);
    border-bottom-color: rgb(192, 1, 255);
    border-left-color: rgb(192, 1, 255); }

    h1, h2, h3, h4, h5, h6 {
    font-family: 'Abel',sans-serif;
    font-weight: 400;
    color: #b883ea; }
}

